export default {
  name: "MainContent",
  data() {
    let checkAge = (rule, value, callback) => {
      if (value > 150) {
        callback(new Error('Input is too large'));
      } else {
        callback();
      }
    };
    let checkDuplicate = (rule, value, callback) => {
      if (this.form.id) {
        return callback();
      }
      this.$axios.get(this.$httpUrl + "/user/findByNo?no=" + this.form.no).then(res => res.data).then(res => {
        if (res.code != 200) {
          callback();
        } else {
          callback(new Error('Account already exist!'));
        }
      });
    };
    return {
      tableData: [],
      pageSize: 10,
      pageNum: 1,
      total: 0,
      name: '',
      //refer v-model里的name
      gender: '',
      genders: [{
        value: '1',
        label: 'Male'
      }, {
        value: '2',
        label: 'Female'
      }],
      centerDialogVisible: false,
      form: {
        name: '',
        no: '',
        password: '',
        age: '',
        phone: '',
        gender: '0',
        roleId: '1'
      },
      rules: {
        name: [{
          required: true,
          message: 'Please input  name',
          trigger: 'blur'
        }],
        no: [{
          required: true,
          message: 'Please input  account number',
          trigger: 'blur'
        }, {
          min: 3,
          max: 8,
          message: 'Length should be 3 to 8',
          trigger: 'blur'
        }, {
          validator: checkDuplicate,
          trigger: 'blur'
        }],
        password: [{
          required: true,
          message: 'Please input  password',
          trigger: 'blur'
        }, {
          min: 3,
          max: 8,
          message: 'Length should be 3 to 8',
          trigger: 'blur'
        }],
        age: [{
          required: true,
          message: 'Please input  age',
          trigger: 'blur'
        }, {
          min: 1,
          max: 3,
          message: 'Length should be 1 to 3',
          trigger: 'blur'
        }, {
          pattern: /^([1-9][0-9]*){1,3}$/,
          message: 'Age must be positive integer',
          trigger: "blur"
        }, {
          validator: checkAge,
          trigger: 'blur'
        }],
        phone: [{
          required: true,
          message: "Phone number cannot be null",
          trigger: "blur"
        }, {
          pattern: /^1[3|4|5|6|7|8|9][0-9]\d{8}$/,
          message: "Please input valid phone number",
          trigger: "blur"
        }]
      }
    };
  },
  methods: {
    resetForm() {
      this.$refs.form.resetFields();
    },
    handleSizeChange(val) {
      console.log(`${val} items per page`);
      this.pageNum = 1;
      this.pageSize = val;
      this.loadPost();
    },
    handleCurrentChange(val) {
      console.log(`current page: ${val}`);
      this.pageNum = val;
      this.loadPost();
    },
    resetParam() {
      this.name = '';
      this.gender = '';
    },
    add() {
      this.centerDialogVisible = true;
      this.$nextTick(() => {
        this.resetForm();
      });
    },
    mod(row) {
      console.log(row);
      this.form.id = row.id;
      this.form.name = row.name;
      this.form.no = row.no;
      this.form.password = '';
      this.form.gender = row.gender + '';
      this.form.phone = row.phone;
      this.form.age = row.age + '';
      this.form.roleId = row.roleId;
      this.centerDialogVisible = true;
    },
    del() {},
    doSave() {
      this.$axios.post(this.$httpUrl + '/user/save', this.form).then(res => res.data).then(res => {
        console.log(res);
        if (res.code == 200) {
          this.$message({
            message: 'Save Successed!',
            type: 'success'
          });
          this.centerDialogVisible = false;
          this.loadPost(); //刷新页面
        } else {
          this.$message({
            message: 'Operation Failed',
            type: 'error'
          });
        }
      });
    },
    doMod() {
      this.$axios.post(this.$httpUrl + '/user/update', this.form).then(res => res.data).then(res => {
        console.log(res);
        if (res.code == 200) {
          this.$message({
            message: 'Save Successed!',
            type: 'success'
          });
          this.centerDialogVisible = false;
          this.loadPost(); //刷新页面
        } else {
          this.$message({
            message: 'Operation Failed',
            type: 'error'
          });
        }
      });
    },
    save() {
      this.$refs.form.validate(valid => {
        if (valid) {
          if (this.form.id) {
            //我什么这个可以表示form输进去的id存不存在
            this.doMod();
          } else {
            this.doSave();
          }
        } else {
          console.log('error submit!!');
          return false;
        }
      });
    },
    loadGet() {
      this.$axios.get(this.$httpUrl + '/user/list').then(res => res.data).then(res => {
        console.log(res);
      });
    },
    loadPost() {
      this.$axios.post(this.$httpUrl + '/user/listPageEn', {
        pageSize: this.pageSize,
        pageNum: this.pageNum,
        param: {
          name: this.name,
          //refer data里的name
          gender: this.gender
        }
      }).then(res => res.data).then(res => {
        console.log(res);
        if (res.code == 200) {
          this.tableData = res.data;
          this.total = res.total;
        } else {
          alert("Resource Get Failed");
        }
      });
    }
  },
  beforeMount() {
    this.loadPost();
  }
};
